import React, { useState } from 'react';



function Loctab() {
    const [location, setLocation] = useState('hi');

    function getLocation() {
        console.log(navigator.geolocation);
        if (navigator.geolocation) {
          setLocation(navigator.geolocation.getCurrentPosition(showPosition));
        } else { 
          setLocation('');
        }
    }

    function showPosition(position) {
        return "Latitude: " + position.coords.latitude + "<br>Longitude: " + position.coords.longitude;
    }

    return(
    <div>
        <button onClick={setLocation(getLocation())}>Try it</button>
        {location}
    </div>);
}

export default Loctab
