import React, { useState } from 'react';

function Loctab() {

    function getLocation() {
        console.log(navigator.geolocation);
        if (navigator.geolocation) {
            console.log(navigator.geolocation.getCurrentPosition(showPosition));
            setLocation(navigator.geolocation.getCurrentPosition(showPosition));
        } else { 
            setLocation('');
        }
    }

    function showPosition(position) {
        console.log(position.coords.latitude);
        setLocation("Latitude: " + position.coords.latitude + "<br>Longitude: " + position.coords.longitude);
    }

    const [location, setLocation] = useState(getLocation());


    return(
    <div>
        <button onClick={() => setLocation(getLocation())}>Refresh</button>
        {location}
    </div>);
}

export default Loctab;
